def get_icon_svg(name: str, color: str = "currentColor") -> str:
    """
    Get SVG icon markup.

    Args:
        name: Icon name (star, commits, prs, issues, etc.)
        color: Fill color for the icon

    Returns:
        SVG icon element
    """
    icon_path = ICONS.get(name, "")
    if not icon_path:
        return ""

    return f"""<svg class="icon" viewBox="0 0 16 16" width="16" height="16" fill="{color}">
      {icon_path}
    </svg>"""
